export enum Decision {
  BUY = 'BUY',
  SELL = 'SELL',
  WAIT = 'WAIT'
}

export interface TradingAnalysis {
  thought_process: string;
  decision: Decision;
  confidence: number;
  entry_zone: string;
  stop_loss: string;
  take_profit: string;
  leverage: string;
}

export interface KlineData {
  time: number;
  open: number;
  high: number;
  low: number;
  close: number;
  volume: number;
}

export interface MarketSnapshot {
  symbol: string;
  price: number;
  change24h: number;
  klines: KlineData[];
}

import { MarketSnapshot, KlineData } from '../types';

const BINANCE_API_BASE = 'https://api.binance.com/api/v3';

export const fetchBinanceData = async (symbol: string = 'BTCUSDT'): Promise<MarketSnapshot> => {
  try {
    // Fetch 24hr Ticker
    const tickerRes = await fetch(`${BINANCE_API_BASE}/ticker/24hr?symbol=${symbol}`);
    const ticker = await tickerRes.json();

    // Fetch Klines (15m interval, 50 limits)
    const klinesRes = await fetch(`${BINANCE_API_BASE}/klines?symbol=${symbol}&interval=15m&limit=50`);
    const klinesRaw = await klinesRes.json();

    const klines: KlineData[] = klinesRaw.map((k: any) => ({
      time: k[0],
      open: parseFloat(k[1]),
      high: parseFloat(k[2]),
      low: parseFloat(k[3]),
      close: parseFloat(k[4]),
      volume: parseFloat(k[5]),
    }));

    return {
      symbol,
      price: parseFloat(ticker.lastPrice),
      change24h: parseFloat(ticker.priceChangePercent),
      klines
    };
  } catch (error) {
    console.error('Market data fetch error:', error);
    throw error;
  }
};

import { GoogleGenAI, Type } from "@google/genai";
import { TradingAnalysis, MarketSnapshot } from '../types';

const SYSTEM_INSTRUCTION = `
You are "Triad Consensus Engine", an elite trading decision-making system for Binance Futures.
Your architecture consists of three internal agents analyzing OHLCV + Order Book context.

YOUR AGENTS:
1. [Chartist]: Analyzes Price Action, support/resistance levels, and trends. Searches for entry opportunities.
2. [Skeptic]: Searches for reasons to CANCEL the trade. Analyzes liquidity traps, divergences, and risk of manipulation.
3. [Risk Manager]: Calculates risk. If the trade is questionable (confidence < 70%), it imposes a veto.

PROCESS (Chain of Thought):
1. Agents first debate among themselves.
2. Then "Soft Voting" (weighted voting) occurs.
3. A final JSON is formed.

Return ONLY a valid JSON object.
`;

export const analyzeMarket = async (snapshot: MarketSnapshot): Promise<TradingAnalysis> => {
  const ai = new GoogleGenAI({ apiKey: process.env.API_KEY || '' });

  const lastKlines = snapshot.klines.slice(-10).map(k => 
    `Time: ${new Date(k.time).toLocaleTimeString()}, O: ${k.open}, H: ${k.high}, L: ${k.low}, C: ${k.close}, V: ${k.volume}`
  ).join('\n');

  const prompt = `
    Market Snapshot:
    Symbol: ${snapshot.symbol}
    Current Price: ${snapshot.price}
    24h Change: ${snapshot.change24h}%

    Recent Price History (15m candles):
    ${lastKlines}

    Task: Provide a consensus trading decision.
  `;

  const response = await ai.models.generateContent({
    model: 'gemini-3-flash-preview',
    contents: prompt,
    config: {
      systemInstruction: SYSTEM_INSTRUCTION,
      responseMimeType: "application/json",
      responseSchema: {
        type: Type.OBJECT,
        properties: {
          thought_process: { type: Type.STRING, description: "Brief summary of agent debates (max 50 words)." },
          decision: { type: Type.STRING, enum: ["BUY", "SELL", "WAIT"] },
          confidence: { type: Type.NUMBER, description: "Confidence score from 0 to 100." },
          entry_zone: { type: Type.STRING, description: "Recommended entry price or range." },
          stop_loss: { type: Type.STRING, description: "Recommended stop loss price." },
          take_profit: { type: Type.STRING, description: "Recommended take profit price." },
          leverage: { type: Type.STRING, description: "Recommended leverage (max 5x)." }
        },
        required: ["thought_process", "decision", "confidence", "entry_zone", "stop_loss", "take_profit", "leverage"]
      }
    }
  });

  try {
    return JSON.parse(response.text);
  } catch (e) {
    console.error("JSON Parse Error from Gemini:", e);
    throw new Error("Failed to parse AI response");
  }
};

import React from 'react';
import { 
  ResponsiveContainer, 
  AreaChart, 
  Area, 
  XAxis, 
  YAxis, 
  Tooltip, 
  CartesianGrid 
} from 'recharts';
import { KlineData } from '../types';

interface PriceChartProps {
  data: KlineData[];
}

const PriceChart: React.FC<PriceChartProps> = ({ data }) => {
  const chartData = data.map(k => ({
    time: new Date(k.time).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' }),
    price: k.close
  }));

  const minPrice = Math.min(...data.map(k => k.close)) * 0.999;
  const maxPrice = Math.max(...data.map(k => k.close)) * 1.001;

  return (
    <div className="h-64 w-full bg-slate-900/50 rounded-xl p-4 border border-slate-800">
      <ResponsiveContainer width="100%" height="100%">
        <AreaChart data={chartData}>
          <defs>
            <linearGradient id="colorPrice" x1="0" y1="0" x2="0" y2="1">
              <stop offset="5%" stopColor="#3b82f6" stopOpacity={0.3}/>
              <stop offset="95%" stopColor="#3b82f6" stopOpacity={0}/>
            </linearGradient>
          </defs>
          <CartesianGrid strokeDasharray="3 3" vertical={false} stroke="#1e293b" />
          <XAxis 
            dataKey="time" 
            axisLine={false} 
            tickLine={false} 
            tick={{ fill: '#64748b', fontSize: 10 }}
            interval="preserveStartEnd"
          />
          <YAxis 
            domain={[minPrice, maxPrice]} 
            hide 
          />
          <Tooltip 
            contentStyle={{ backgroundColor: '#0f172a', border: '1px solid #1e293b', borderRadius: '8px' }}
            itemStyle={{ color: '#f8fafc' }}
          />
          <Area 
            type="monotone" 
            dataKey="price" 
            stroke="#3b82f6" 
            fillOpacity={1} 
            fill="url(#colorPrice)" 
            strokeWidth={2}
          />
        </AreaChart>
      </ResponsiveContainer>
    </div>
  );
};

export default PriceChart;

import React, { useState, useEffect, useCallback } from 'react';
import { fetchBinanceData } from './services/marketData';
import { analyzeMarket } from './services/gemini';
import { MarketSnapshot, TradingAnalysis } from './types';
import PriceChart from './components/PriceChart';
import AnalysisResult from './components/AnalysisResult';

const App: React.FC = () => {
  const [symbol, setSymbol] = useState('BTCUSDT');
  const [marketSnapshot, setMarketSnapshot] = useState<MarketSnapshot | null>(null);
  const [analysis, setAnalysis] = useState<TradingAnalysis | null>(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const symbols = ['BTCUSDT', 'ETHUSDT', 'SOLUSDT', 'BNBUSDT', 'ADAUSDT'];

  const refreshData = useCallback(async (targetSymbol: string) => {
    try {
      const data = await fetchBinanceData(targetSymbol);
      setMarketSnapshot(data);
      setError(null);
    } catch (err) {
      setError("Failed to fetch market data. Please check your connection.");
    }
  }, []);

  useEffect(() => {
    refreshData(symbol);
    const interval = setInterval(() => refreshData(symbol), 30000); // refresh every 30s
    return () => clearInterval(interval);
  }, [symbol, refreshData]);

  const handleRunAnalysis = async () => {
    if (!marketSnapshot) return;
    setLoading(true);
    setError(null);
    try {
      const result = await analyzeMarket(marketSnapshot);
      setAnalysis(result);
    } catch (err) {
      setError("AI analysis failed. Ensure your API key is configured.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen p-4 md:p-8 max-w-6xl mx-auto space-y-8">
      {/* Header */}
      <header className="flex flex-col md:flex-row justify-between items-start md:items-center gap-6">
        <div>
          <h1 className="text-3xl font-bold text-white flex items-center gap-2">
            <span className="bg-blue-600 p-2 rounded-lg">
              <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 19v-6a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2a2 2 0 002-2zm0 0V9a2 2 0 012-2h2a2 2 0 012 2v10m-6 0a2 2 0 002 2h2a2 2 0 002-2m0 0V5a2 2 0 012-2h2a2 2 0 012 2v14a2 2 0 01-2 2h-2a2 2 0 01-2-2z"/></svg>
            </span>
            Triad Core <span className="text-blue-500 italic">Engine</span>
          </h1>
          <p className="text-slate-400 mt-1">Institutional-Grade Crypto Ensemble Logic</p>
        </div>

        <div className="flex items-center gap-4 bg-slate-900 p-2 rounded-xl border border-slate-800">
          {symbols.map(s => (
            <button
              key={s}
              onClick={() => {
                setSymbol(s);
                setAnalysis(null);
              }}
              className={`px-4 py-2 rounded-lg text-sm font-semibold transition-all ${
                symbol === s 
                  ? 'bg-blue-600 text-white shadow-lg shadow-blue-900/40' 
                  : 'text-slate-500 hover:text-slate-200 hover:bg-slate-800'
              }`}
            >
              {s.replace('USDT', '')}
            </button>
          ))}
        </div>
      </header>

      {/* Main Dashboard */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
        {/* Left Column: Market Info */}
        <div className="lg:col-span-2 space-y-6">
          <div className="bg-slate-900 rounded-2xl p-6 border border-slate-800 relative overflow-hidden group">
            <div className="absolute top-0 right-0 p-4 opacity-5 group-hover:opacity-20 transition-opacity">
               <svg className="w-32 h-32" fill="currentColor" viewBox="0 0 24 24"><path d="M16 6l2.29 2.29-4.88 4.88-4-4L2 16.59 3.41 18l6-6 4 4 6.3-6.29L22 12V6z"/></svg>
            </div>

            <div className="flex justify-between items-end mb-6">
              <div>
                <h2 className="text-slate-500 text-sm font-semibold uppercase tracking-wider mb-1">Live Market Price</h2>
                <div className="flex items-baseline gap-3">
                  <span className="text-4xl font-mono font-bold text-white tracking-tight">
                    ${marketSnapshot?.price.toLocaleString() ?? '---'}
                  </span>
                  <span className={`text-lg font-semibold ${marketSnapshot && marketSnapshot.change24h >= 0 ? 'text-green-400' : 'text-red-400'}`}>
                    {marketSnapshot && marketSnapshot.change24h >= 0 ? '+' : ''}{marketSnapshot?.change24h}%
                  </span>
                </div>
              </div>
              <button 
                onClick={handleRunAnalysis}
                disabled={loading || !marketSnapshot}
                className="bg-blue-600 hover:bg-blue-500 disabled:opacity-50 disabled:cursor-not-allowed text-white px-8 py-3 rounded-xl font-bold flex items-center gap-2 shadow-xl shadow-blue-900/30 transition-all active:scale-95"
              >
                {loading ? 'Analyzing...' : 'üß† Start Analysis'}
                <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M13 10V3L4 14h7v7l9-11h-7z"/></svg>
              </button>
            </div>

            {marketSnapshot && <PriceChart data={marketSnapshot.klines} />}
          </div>

          <AnalysisResult analysis={analysis} loading={loading} />
        </div>

        {/* Right Column: Engine Status & Log */}
        <div className="space-y-6">
          <div className="bg-slate-900 rounded-2xl border border-slate-800 overflow-hidden">
            <div className="p-4 border-b border-slate-800 bg-slate-800/50">
              <h3 className="font-bold text-slate-200">System Monitoring</h3>
            </div>
            <div className="p-6 space-y-4">
