               <p className="text-[10px] text-slate-600 text-center uppercase tracking-widest mt-4">Protected by Triad Secure Bridge</p>
            </div>
          </div>
        </div>
      </div>

      {/* Footer */}
      <footer className="text-center text-slate-600 text-xs py-8">
        &copy; 2024 Triad Core AI. Educational Purpose Only. Trading carries high risk.
      </footer>
    </div>
  );
};

export default App;
